import React, { Component } from 'react';
import validator from 'validator';
import InputMask from 'react-input-mask';
import s from './css/Inputs-left.module.css'


class Inputs_left extends Component {
  constructor(props) {
    super(props);
    this.state = {
      phone: '',
      password: '',
      isValidPhone: false,
      isValidPassword: false
    };
    this.handlerSubmit = this.handlerSubmit.bind(this);
    // Создаю ref, чтобы было удобно работать с input'ом
    this.inputNameRef = React.createRef();
  }
  handlerSubmit(event) {
    event.preventDefault();
    const form = event.target;
    const inputsData = form.querySelectorAll('input');
    const arrDataFromForm = {};
    Array.from(inputsData).map((e) => {
      const name = e.name;
      const value = (name !== 'phone')
        ? e.value // Записываю пароли
        : e.value.replace(/\D/g, '').replace(/^7/, '8'); // Чищу номера телефона от лишних символов
      arrDataFromForm[name] = value;
    });
    // Проверка номера на валидность
    if (validator.isMobilePhone(arrDataFromForm["phone"], ['ru-RU'])) {
      // Изменил state
      this.setState({
        isValidPhone: true,
        phone: arrDataFromForm["phone"]
      });
    } else {
      // Предупреждаю, что номер неверный
      alert('Неверный номер телефона');
      // Подсвечиваю input с номером телефона для удобства
      this.inputNameRef.current.focus();
    }
  }

  render() {
    return (
      <form method="post" onSubmit={this.handlerSubmit}>
        <div className={s.block}>
          <div className={s.flex}>
            <label>Імʼя<input type="text" required></input></label>

            <label>Фамілія<input type="text" required></input></label>
          </div>
          <div>
            <label >Назва компанії, організаці<input type="text" required></input></label>
            <label >Email-адрес<input type="email"></input></label>
            <label >Номер телефону<input ref={this.inputNameRef} id="name" name="phone" type="tel" /></label>
            <InputMask {...this.props} mask="+4\9 99 999 99" maskChar=" " />
          </div>
        </div >
        <button type="submit">отправить</button>
      </form>
    )
  }
}

export default Inputs_left;
